	private static int getFloor(String input){
		int floor = 0;
		for (int i = 0; i < input.length(); i++){
			if (input.charAt(i) == '('){
				floor++;
			}
			if (input.charAt(i) == ')'){
				floor--;
			}
		}
		return floor;
	}
	
	private static int getBasementPosition(String input){
		int floor = 0;
		for (int i = 0; i < input.length(); i++){
			if (input.charAt(i) == '('){
				floor++;
			}
			if (input.charAt(i) == ')'){
				floor--;
			}
			if (floor == -1){
				return (i+1);
			}
		}
		return floor;
	}

